%% AGE CAT 1
%%RIGHT SIDE first
clear R G B A full_cell 
r=[1:360]'; rr=r((consistent1==1 & adjr2>0 & r'>180) | (inconsistent1==1 & adjr2>0 & r'>180)); 
for index=1:length(rr);
    roi=rr(index);
    %e.g. t=2; maxt=4 => then color should be half way to the max of the range:
    if inconsistent1(roi)==1
    blue_range=0:255;
    green_range=0:255;
    tstat_relative=1-abs(tstat1(roi))/(max(tstat1)+0.1);
    color_relative=round(tstat_relative*256);
    
    R(index,1)=255;
    G(index,1)=round(green_range(color_relative));
    B(index,1)=blue_range(color_relative);
    A(index,1)=0;
    full_cell{index, 1}=index; full_cell{index, 2}=rlabel_names{roi-180};full_cell{index, 3}=R(index);    full_cell{index, 4}=G(index);    full_cell{index, 5}=B(index);     full_cell{index, 6}=A(index);
    
    elseif consistent1(roi)==1
    red_range=0:255;
    green_range=0:255;
    tstat_relative=1-abs(tstat1(roi))/(max(tstat1)+0.1);
    color_relative=round(tstat_relative*256);
    
    R(index,1)=red_range(color_relative);
    G(index,1)=round(green_range(color_relative));
    B(index,1)=204;
    A(index,1)=0;
    full_cell{index, 1}=index; full_cell{index, 2}=rlabel_names{roi-180};full_cell{index, 3}=R(index);    full_cell{index, 4}=G(index);    full_cell{index, 5}=B(index);     full_cell{index, 6}=A(index);
